/** @jsx jsx */
// enables theme-ui in <jsx>
import { useState } from 'react';
import Modal from 'react-modal';
import { jsx, Box, Button, Flex } from 'theme-ui';

const Contact = () => {
  const [modalIsOpen, SetModalIsOpen] = useState(false);

  const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
  };

  Modal.setAppElement('body');
  return (
    <>
      <Button onClick={() => SetModalIsOpen(true)}>Hit us up</Button>
      <Box
      // sx={{
      //   top: '30vh',
      //   left: '80vw',
      //   maxWidth: '50vw',
      //   bg: 'white',
      // }}
      >
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={() => SetModalIsOpen(false)}
          style={
            ({ customStyles },
            {
              overlay: {
                background: 'grey',
              },
            })
          }
        >
          <Flex
            sx={{
              // position: 'fixed',

              opacity: 0.9,
              justifyContent: 'space-between',
            }}
          >
            <h2>Contact us!</h2>
            <Button onClick={() => SetModalIsOpen(false)}>Close dialog</Button>
          </Flex>
          <textarea name='email-us' cols='30' rows='10'></textarea>
        </Modal>
      </Box>
    </>
  );
};

export default Contact;
