/** @jsx jsx */
// enables theme-ui in <jsx>
import { useState } from 'react';
import Modal from 'react-modal';
import { jsx, Box, Button, Flex, Textarea } from 'theme-ui';

const Contact = () => {
  const [modalIsOpen, SetModalIsOpen] = useState(false);

  const customStyles = {
    content: {
      position: 'fixed',
      minWidth: '50vw',
      maxWidth: '80vw',
      maxHeight: '70vh',
      top: '50vh',
      left: '50vw',
      right: 'auto',
      bottom: 'auto',
      zIndex: '100',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
  };

  Modal.setAppElement('body');
  return (
    <>
      <Button
        sx={{
          minHeight: '2em',
          maxHeight: '2.5em',
        }}
        onClick={() => SetModalIsOpen(true)}
      >
        Hit us up
      </Button>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => SetModalIsOpen(false)}
        style={customStyles}
      >
        <Flex
          sx={{
            // position: 'fixed',

            opacity: 0.9,
            justifyContent: 'space-between',
            alignContent: 'center',
            alignItems: 'center',
          }}
        >
          <h2>Tell us wassup</h2>
          <Button
            onClick={() => SetModalIsOpen(false)}
            sx={{
              maxHeight: '2.5em',
              maxWidth: '2.5em',
              alignContent: 'center',
            }}
          >
            &times;
          </Button>
        </Flex>
        <Textarea
          variant='modal'
          name='email-us'
          cols='30'
          rows='10'
          placeholder='say what...'
          autoFocus
        />
        <Button>Submit</Button>
      </Modal>
    </>
  );
};

export default Contact;
